import { Component, input } from '@angular/core';
import { cn } from '@/lib/utils';

@Component({
  selector: 'div[razCard]',
  standalone: true,
  template: `<ng-content />`,
  host: {
    '[class]': 'computedClass()',
  },
})
export class CardComponent {
  class = input<string>('');

  computedClass() {
    return cn(
      'rounded-lg border bg-card text-card-foreground shadow-sm',
      this.class()
    );
  }
}

